import axios from "axios";

const Post = props => {
  let arraySprites = Object.values(props.pokemon.sprites);
  console.log(props.pokemon.types);
  return (
    <div className="row">
      <div className="col-md-4">
        <div style={{ width: "18rem" }}>
          <img src={props.imageUrl} className="card-img-top" alt="..." />
          <div className="card-body">
            <h5 className="card-title text-uppercase">{props.pokemon.name}</h5>
            <p className="card-text">
              {props.pokemon.types.map((a, i) => {
                return <strong key={i}>| {a.type.name} | </strong>;
              })}
            </p>
            <a href="#" className="btn btn-primary">
              Go somewhere
            </a>
          </div>
        </div>
      </div>
      <div className="col-md-8">
        {arraySprites.map((sprite, i) => {
          return <img src={sprite} key={i}></img>;
        })}
      </div>
    </div>
  );
};

Post.getInitialProps = async function(context) {
  const { id } = context.query;
  let padddedId = id.padStart(3, "0");
  let image =
    "https://assets.pokemon.com/assets/cms2/img/pokedex/full/" +
    padddedId +
    ".png";
  const response = await axios.get(`https://pokeapi.co/api/v2/pokemon/${id}`);

  const response1 = await axios.get(
    "https://pokeapi.co/api/v2/pokemon/?offset=0&limit=151"
  );

  let sideBarProps = response1.data.results;

  return {
    pokemon: response.data,
    imageUrl: image,
    pokemons: sideBarProps
  };
};

export default Post;
