import Layout from "../../components/layout";
import axios from "axios";

const Post = props => {
  let arraySprites = Object.values(props.pokemon.sprites);
  console.log(props.pokemon.types);
  return (
    <Layout>
      <div className="container">
        <div className="row">
          <div className="col-md-4">
            <div className="card" style={{ width: "18rem" }}>
              <img src={props.imageUrl} className="card-img-top" alt="..." />
              <div className="card-body">
                <h5 className="card-title text-uppercase">{props.name}</h5>
                <p className="card-text">
                  {/* {props.pokemon.types.map(a => {
                    return a.type;
                  })} */}
                </p>
                <a href="#" className="btn btn-primary">
                  Go somewhere
                </a>
              </div>
            </div>
          </div>
          <div className="col-md-8">
            <div className="row text-center">
              {arraySprites.map((sprite, i) => {
                return (
                  <div>
                    <img src={sprite} key={i}></img>
                  </div>
                );
              })}
            </div>
          </div>
        </div>
      </div>
    </Layout>
  );
};

Post.getInitialProps = async function(context) {
  const { id } = context.query;
  let padddedId = id.padStart(3, "0");
  let image =
    "https://assets.pokemon.com/assets/cms2/img/pokedex/full/" +
    padddedId +
    ".png";
  const response = await axios.get(`https://pokeapi.co/api/v2/pokemon/${id}`);
  return {
    pokemon: response.data,
    imageUrl: image
  };
};

export default Post;
