import Link from "next/link";

const linkStyle = {
  marginRight: 15
};

const Sidebar = props => {
  return (
    <div>
      {" "}
      <ul>
        {props.pokemons.map((pokemon, index) => (
          <li key={index + 1}>
            <Link href="/p/[id]" as={`/p/${index + 1}`}>
              <a>{pokemon.name}</a>
            </Link>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Sidebar;
