import Link from "next/link";

const Sidebar = props => {
  return (
    <div
      className="sidebar"
      style={{ height: "calc(100vh - 56px)", overflow: "auto" }}
    >
      <ul>
        {props.pokemon.map((pokemon, index) => (
          <li key={index + 1} className="m-2">
            <Link href="/p/[id]" as={`/p/${index + 1}`}>
              <a>{pokemon.name}</a>
            </Link>
          </li>
        ))}
      </ul>
    </div>
  );
};

Sidebar.getInitialProps = async function() {
  let props = await getInitialProps({
    pokemons: getPokemons()
  })

  return props
};

export default Sidebar;
